import React, { Component } from "react";
import { useSpring, animated } from "react-spring";
import { Spring } from "react-spring/renderprops";

export default class NodeCircle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: props.value,

            colStart: props.colStart,
            colEnd: props.colEnd,
            rowStart: props.rowStart,
            rowEnd: props.rowEnd,

            border: "1px solid white",
            boxShadow: "0px 0px 3px 0px white",
            color: "white",

            beginGreen: false,
            beginRed: false
        };

        this.greenAnimation = useSpring({
            from: {
                border: "1px solid white",
                boxShadow: "0px 0px 3px 0px white",
                color: "white",
            },
            to: {
                border: "1px solid #4ecca3",
                boxShadow: "0px 0px 3px 3px #4ecca3",
                color: "#4ecca3",
            }
        });

        this.animateGreen = this.animateGreen.bind(this);
        this.animateRed = this.animateRed.bind(this);
    }

    animateGreen() {
        this.setState(prevState => (
            { beginGreen: !prevState.beginGreen }))

        // this.setState({
        //     border: "1px solid #4ecca3",
        //     boxShadow: "0px 0px 3px 3px #4ecca3",
        //     color: "#4ecca3",
        // })
    }

    animateRed() {
        this.setState({
            border: "1px solid #ff0000",
            boxShadow: "0px 0px 3px 3px #ff0000",
            color: "#ff0000",
        })
    }

    render() {
        const nodeStyle = {
            height: "40px",
            width: "40px",
            gridColumnStart: this.state.colStart,
            gridColumnEnd: this.state.colEnd,
            gridRowStart: this.state.rowStart,
            gridRowEnd: this.state.rowEnd,

            borderRadius: "50%",
            border: this.state.border,
            boxShadow: this.state.boxShadow,
            color: this.state.color,
            fontWeight: "bold",

            display: "flex",
            justifyContent: "center",
            alignItems: "center"
        };

        return (
            <Spring
                native
                from={{
                    border: "1px solid white",
                    boxShadow: "0px 0px 3px 0px white",
                    color: "white",
                }}
                to={{
                    border: "1px solid #4ecca3",
                    boxShadow: "0px 0px 3px 3px #4ecca3",
                    color: "#4ecca3",
                }}>
                <animated.div>
                    {this.state.value}
                </animated.div>
            </Spring>

        );
    }
}
