import Node from "./NodeClass"

export default class AVLTree {
    constructor() {
        this.root = null;
    }

    height(node) {
        return node === null ? -1 : node.height;
    }

    insert(val) {
        this.root = this.insertHelper(this.root, val);
    }

    insertHelper(node, val) {
        if (node === null) return new Node(val);

        if (val < node.value) node.left = this.insertHelper(node.left, val);
        else if (val > node.value) node.right = this.insertHelper(node.right, val);

        return node;
    }

    levelOrder() {
        if (!this.root) {
            return;
        }

        let queues = [
            [],
            []
        ];
        let current_queue = queues[0];
        let next_queue = queues[1];

        current_queue.push(this.root);
        let level_number = 0;

        let currKey = 1;

        while (current_queue.length > 0) {
            let temp = current_queue.shift();
            temp.key = currKey;
            currKey += 1;
            if (temp.left) {
                next_queue.push(temp.left);
            }

            if (temp.right) {
                next_queue.push(temp.right);
            }

            if (current_queue.length === 0) {
                level_number++;
                current_queue = queues[level_number % 2];
                next_queue = queues[(level_number + 1) % 2];
            }
        }
    }
}
