import React, { Component } from "react";

export default class NodeCircle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: props.value,
            parent: props.parent,
            left: props.left,
            right: props.right,
            colStart: props.colStart,
            colEnd: props.colEnd,
            rowStart: props.rowStart,
            rowEnd: props.rowEnd
        };
    }
    render() {
        const nodeStyle = {
            height: "40px",
            width: "40px",
            gridColumnStart: this.state.colStart,
            gridColumnEnd: this.state.colEnd,
            gridRowStart: this.state.rowStart,
            gridRowEnd: this.state.rowEnd,
        
            // backgroundColor: "#03DAC5",
            borderRadius: "50%",
            border: "1px solid white",
            boxShadow: "0px 0px 2px 1px white",
            color: "white",
            fontWeight: "bold",
        
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
        }

        return (
            <div style={nodeStyle}>
                {this.state.value}
            </div>
        );
    }
}
