import React, { Component } from "react";

export default class NodeCircle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: props.value,
            parent: props.parent,
            left: props.left,
            right: props.right,
            colStart: props.colStart,
            colEnd: props.colEnd,
            rowStart: props.rowStart,
            rowEnd: props.rowEnd,
            nodeStyle: {
                height: "40px",
                width: "40px",
                gridColumnStart: props.colStart,
                gridColumnEnd: props.colEnd,
                gridRowStart: props.rowStart,
                gridRowEnd: props.rowEnd,

                // backgroundColor: "#03DAC5",
                borderRadius: "50%",
                border: "1px solid white",
                boxShadow: "0px 0px 3px 0px white",
                color: "white",
                fontWeight: "bold",

                display: "flex",
                justifyContent: "center",
                alignItems: "center"
            }
        };

        this.setGreenShadow = this.setGreenShadow.bind(this);
    }

    setGreenShadow() {
        this.setState({
            nodeStyle: {
                height: "40px",
                width: "40px",
                gridColumnStart: this.state.colStart,
                gridColumnEnd: this.state.colEnd,
                gridRowStart: this.state.rowStart,
                gridRowEnd: this.state.rowEnd,

                // backgroundColor: "#03DAC5",
                borderRadius: "50%",
                border: "1px solid #4ecca3",
                boxShadow: "0px 0px 3px 0px #4ecca3",
                color: "#4ecca3",
                fontWeight: "bold",

                display: "flex",
                justifyContent: "center",
                alignItems: "center"
            }
        })
    }

    render() {
        return (
            <div style={this.state.nodeStyle} onMouseOver={this.setGreenShadow}>
                {this.state.value}
            </div>
        );
    }
}
