import React, { Component } from "react";
import ArrowCreate, { DIRECTION } from "react-arrows";

export default class Arrow extends Component {
    constructor(props) {
        super(props);
        this.state = {

        };
    }

    render() {
        const containerStyle = {
            gridColumnStart: this.props.colStart,
            gridColumnEnd: this.props.colEnd,
            gridRowStart: this.props.rowStart,
            gridRowEnd: this.props.rowEnd,
            position: "relative",
        }

        return (
            <div style={containerStyle}>
                {/* <div id={`${this.props.id}x1`} className={this.props.direction === 1 ? "topLeft" : "topRight"}></div> */}
                {/* <div id={`${this.props.id}x2`} className={this.props.direction === 1 ? "bottomRight" : "bottomLeft"}></div> */}
                <div id={`${this.props.id}x1`} className="topLeft"></div>
                <div id={`${this.props.id}x2`} className="bottomRight"></div>

                <ArrowCreate
                    className="arrow"
                    from={{
                        direction: DIRECTION.BOTTOM,
                        node: () => document.getElementById(`${this.props.id}x1`),
                        translation: [0, 0]
                    }}
                    to={{
                        direction: DIRECTION.TOP,
                        node: () => document.getElementById(`${this.props.id}x2`),
                        translation: [0, 0]
                    }}
                    head={{
                        distance: 0.99
                    }}
                />
            </div>
        )
    }
}
