import React, { Component } from "react";
import ArrowCreate, { DIRECTION } from "react-arrows";

export default class Arrow extends Component {
    constructor(props) {
        super(props);
        this.state = {

        };
    }

    render() {
        return (
            <div>
                let from = <div className={this.props.direction===1 ? "topLeft" : "topRight" }></div>
                <div className={this.props.direction===1 ? "bottomRight" : "bottomLeft" }></div>

                <ArrowCreate
                    className="arrow"
                    from={{
                        direction: DIRECTION.BOTTOM,
                        node: () => this.state.from,
                        translation: [0, 0]
                    }}
                    to={{
                        direction: DIRECTION.TOP_LEFT,
                        node: () => this.state.to,
                        translation: [0, 0]
                    }}
                    head={{
                        distance: 0.99,
                        size: 10,
                    }}
                />
            </div>
        )
    }
}
