import React, { Component } from "react";

export default class NodeCircle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: props.value,

            colStart: props.colStart,
            colEnd: props.colEnd,
            rowStart: props.rowStart,
            rowEnd: props.rowEnd,

            border: "1px solid white",
            boxShadow: "0px 0px 3px 0px white",
            color: "white",

            beginGreen: false,
            beginRed: false
        };

        this.animateGreen = this.animateGreen.bind(this);
        this.animateRed = this.animateRed.bind(this);
    }

    animateGreen() {
        this.setState(prevState => (
            { beginGreen: !prevState.beginGreen }))
    }

    animateRed() {
        this.setState({
            border: "1px solid #ff0000",
            boxShadow: "0px 0px 3px 3px #ff0000",
            color: "#ff0000",
        })
    }

    render() {
        const nodeStyle = {
            height: "40px",
            width: "40px",
            gridColumnStart: this.state.colStart,
            gridColumnEnd: this.state.colEnd,
            gridRowStart: this.state.rowStart,
            gridRowEnd: this.state.rowEnd,

            borderRadius: "50%",
            border: this.state.border,
            boxShadow: this.state.boxShadow,
            color: this.state.color,
            fontWeight: "bold",

            display: "flex",
            justifyContent: "center",
            alignItems: "center"
        };

        nodeStyle.animation = "changeToGreen 5s";


        return (
            <div style={nodeStyle}>
                {this.state.value}
            </div>
        );
    }
}
