import React, { Component } from "react";
import ArrowCreate, { DIRECTION } from "react-arrows";

export default class Arrow extends Component {
    constructor(props) {
        super(props);
        this.state = {
            
        }
    }

    render() {
        return (
            <ArrowCreate
                className="arrow"
                from={{
                    direction: DIRECTION.BOTTOM,
                    node: () => this.state.from,
                    translation: [0, 0]
                }}
                to={{
                    direction: DIRECTION.TOP_LEFT,
                    node: () => this.state.to,
                    translation: [0, 0]
                }}
                head={{
                    distance: 0.99,
                    size: 10,
                }}
            />
        )
    }
}
