import React, { Component } from "react";

import "./componentStyles.css";
import NodeCircle from "./NodeCircle.jsx";
import nodesPositions from "./NodesPositions.js";
import Node from "./NodeClass.js";
import AVLTree from "./AVLTreeClass.js";

export default class AVLTreeVisualizer extends Component {
    constructor() {
        super();
        this.state = {
            number: "",
            tree: new AVLTree(),
            nodesArray: []
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }

    handleClick() {
        if (isNaN(this.state.number)) alert("Please insert a number instead");
        if (this.state.number > 9999) alert("Please enter a value less than 10,000");
        else {
            this.state.tree.insert(this.state.number);
            this.spawnNode(this.state.tree.getNode(this.state.number));
        }
    }

    spawnNode(node) {
        this.state.nodesArray[node.key] = nodesPositions.map(item => {
            if (node.key === item.id) {
                return <NodeCircle colStart={item.colStart} colEnd={item.colEnd} rowStart={item.rowStart} rowEnd={item.rowEnd}/>
            }
        })
    }

    render() {
        const nodes = nodesPositions.map(item => <NodeCircle colStart={item.colStart} colEnd={item.colEnd} rowStart={item.rowStart} rowEnd={item.rowEnd} />);

        return (
            <div>
                <div>
                    <form>
                        <input type="text" value={this.state.number} name="number" placeholder="Insert a Value" onChange={this.handleChange} />
                    </form>
                    <button onClick={this.handleClick}>Insert!</button>
                </div>

                <div className="grid">
                    {nodes}
                </div>
            </div>
        );
    }
}
