import React, { Component } from "react"

import AVLTree from "./AVLTreeClass";

export default class InsertionForm extends Component {
    constructor() {
        super();
        this.state = {
            number: "",
            tree: new AVLTree()
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }

    handleClick() {
        if (isNaN(this.state.number)) alert("Please insert a number")
        else this.state.tree.insert(this.state.number);
    }

    render() {
        return (
            <div>
                <form>
                    <input type="text" value={this.state.number} name="number" placeholder="Insert a Value" onChange={this.handleChange} />
                </form>
                <button onClick={this.handleClick}>Insert!</button>
            </div>
        )
    }
}
