import React, { Component } from "react";

export default class NodeCircle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: props.value,

            colStart: props.colStart,
            colEnd: props.colEnd,
            rowStart: props.rowStart,
            rowEnd: props.rowEnd,

            borderRadius: "50%",
            border: "1px solid white",
            boxShadow: "0px 0px 3px 0px white",
            color: "white",
            fontWeight: "bold",

            display: "flex",
            justifyContent: "center",
            alignItems: "center"
        };

        this.setGreenShadow = this.setGreenShadow.bind(this);
    }

    setGreen() {
        this.setState({
            border: "1px solid #4ecca3",
            boxShadow: "0px 0px 3px 3px #4ecca3",
            color: "#4ecca3",
        })
    }

