import React, { Component } from "react";
import arrowCreate, { DIRECTION } from "react-arrows";

export default class Arrow extends Component {
    constructor(props) {
        super(props);
        this.state = {
            from: props.from,
            to: props.to,
            colStart: props.colStart,
            colEnd: props.colEnd,
            rowStart: props.rowStart,
            rowEnd: props.rowEnd
        }
    }

    render() {
        return (
            arrowCreate({
                className: "arrow",
                from: {
                  direction: DIRECTION.BOTTOM,
                  node: () => this.props.from,
                  translation: [0, 0]
                },
                to: {
                  direction: DIRECTION.TOP_LEFT,
                  node: () => this.props.to,
                  translation: [0, 0]
                },
                head: {
                  distance: 0.99,
                  size: 10,
                }
              }).node
        )
    }
}
