import Node from "./NodeClass"

export default class AVLTree {
    constructor() {
        this.root = null;
    }

    height(node) {
        return node === null ? -1 : node.height;
    }

    insert(val) {
        this.root = this.insertRec(this.root, val);
    }

    insertRec(node, val) {
        if (node === null) return (node = new Node(val));

        if (val < node.value) node.left = this.insertRec(node.left, val);
        else if (val > node.value) node.right = this.insertRec(node.right, val);
        
        return node;
    }
}
