import React, { Component, createRef } from "react";

import "./componentStyles.css";
import NodeCircle from "./NodeCircle.jsx";
// import Arrow from "./Arrow.jsx"
import nodesPositions from "./NodesPositions.js";
import AVLTree from "./AVLTreeClass.js";

export default class AVLTreeVisualizer extends Component {
    constructor() {
        super();
        this.state = {
            number: "",
            tree: new AVLTree(),
            nodesArray: [],
        };
        
        this.nodeRef = createRef();

        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.spawnNode = this.spawnNode.bind(this);
        this.pathAnimation = this.pathAnimation.bind(this);
        this.wait = this.wait.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: Number(value)
        })
    }

    handleClick(event) {
        event.preventDefault();
        if (isNaN(this.state.number)) alert("Please insert a number instead");
        else if (this.state.tree.size === 15) alert("Maximum tree size of 15 nodes has been reached!");
        else if (this.state.number > 9999) alert("Please enter a value less than 10,000");
        else {
            const value = this.state.number;
            this.state.tree.insert(value);
            if (this.state.tree.height > 3) {
                alert("Inserting this value will result in a total depth of 5 levels. Maximum depth allowed is 4.");
                this.state.tree.delete(value);
            }
            else this.spawnNode(this.state.tree.getNode(value));
        }
    }

    pathAnimation(key) {
        if (this.state.nodesArray.length > 0) {
            this.nodeRef1.current.animateGreen();
        }
    }

    wait(ms){
        var start = new Date().getTime();
        var end = start;
        while(end < start + ms) {
          end = new Date().getTime();
       }
     }

    spawnNode(node) {
        setTimeout(this.pathAnimation(node.key), 3000);
        const tempNodeInfo = nodesPositions[node.key - 1];
        this[`nodeRef${node.key}`] = createRef();
        const tempNodeCircle = <NodeCircle ref={this[`nodeRef${node.key}`]} key={node.key} value={node.value} colStart={tempNodeInfo.colStart} colEnd={tempNodeInfo.colEnd} rowStart={tempNodeInfo.rowStart} rowEnd={tempNodeInfo.rowEnd} />

        let nodesArray = [...this.state.nodesArray];
        nodesArray[node.key] = tempNodeCircle;

        this.setState({ nodesArray });
    }

    render() {
        return (
            <div>
                <div>
                    <form onSubmit={this.handleClick}>
                        <input type="text" value={this.state.number} name="number" placeholder="Insert a Value" onChange={this.handleChange} />
                        <button type="submit">Insert!</button>
                    </form>
                </div>

                <div className="grid">
                    {console.log(this.state.nodesArray)}
                    {this.state.nodesArray}
                    {/* <NodeCircle value={"test"} colStart={19} colEnd={20} rowStart={1} rowEnd={2} /> */}
                </div>
            </div>
        );
    }
}
