import Node from "./NodeClass"

export default class AVLTree {
    constructor() {
        this.root = null;
        this.size = 0;
    }

    height(node) {
        return node === null ? -1 : node.height;
    }

    getNode(val) {
        let current = this.root;
        while (current) {
            if (val === current.value) {
                return current;
            }
            if (val < Number(current.value)) {
                current = current.left;
            } else {
                current = current.right;
            }
        }
        return current;
    }

    insert(val) {
        this.root = this.insertHelper(this.root, val);
        this.setKey(val);
        this.size += 1;
    }

    insertHelper(node, val) {
        if (node === null) return new Node(val);

        if (val < node.value) node.left = this.insertHelper(node.left, val);
        else if (val > node.value) node.right = this.insertHelper(node.right, val);

        return node;
    }

    setKey(val) {
        let current = this.root;
        let binaryArray = [1];
        while (current) {
            if (val === current.value) {
                break;
            }
            if (val > current.value) {
                current = current.right;
                binaryArray.push(1);
            } else {
                current = current.left;
                binaryArray.push(0);
            }
        }
        const binaryString = binaryArray.join("");
        current.key = parseInt(binaryString, 2);
    }
}
