import React, { Component } from "react";
import ArrowCreate, { DIRECTION } from "react-arrows";

export default class Arrow extends Component {
    constructor(props) {
        super(props);
        this.state = {

        };
    }

    render() {
        let from = <div className={this.props.direction === 1 ? "topLeft" : "topRight"}></div>;
        let to = <div className={this.props.direction === 1 ? "bottomRight" : "bottomLeft"}></div>
        const containerStyle = {
            gridColumnStart: this.props.colStart,
            gridColumnEnd: this.props.colEnd,
            gridRowStart: this.props.rowStart,
            gridRowEnd: this.props.rowEnd,
            positions: "relative"
        }

        return (
            <div style={containerStyle}>
                {from}
                {to}

                <ArrowCreate
                    className="arrow"
                    from={{
                        direction: DIRECTION.BOTTOM_RIGHT,
                        node: () => document.getElementById("from"),
                        translation: [0, 0]
                    }}
                    to={{
                        direction: DIRECTION.TOP_LEFT,
                        node: () => document.getElementById("to"),
                        translation: [0, 0]
                    }}
                    head={{
                        distance: 0.99
                    }}
                />
            </div>
        )
    }
}
